import { createContext, useContext, useEffect, useState } from "react";

// 1. Context
export const AuthContext = createContext();

// 2. Provider
export const AuthProvider = ({ children }) => {
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [user, setUser] = useState(null);

  let isLoggedIn = !!token;

  console.log('isLoggedIn', isLoggedIn);

  const storeTokenInLS = (serverToken) => {
    localStorage.setItem("token", serverToken);
    setToken(serverToken);  // Store the token in state as well
  };

  // Logout functionality
  const LogoutUser = () => {
    setToken(null);  // Clear token in state
    localStorage.removeItem('token');
    setUser(null);  // Clear user data as well
  };

  // JWT Authentication - fetching current user data
  const userAuthentication = async () => {
    if (!token) return;  // Don't try fetching if no token is present

    try {
      const response = await fetch('http://localhost:3000/api/v1/user', {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
        }
      });

      if (response.ok) {
        const data = await response.json();
        console.log("user data", data.userData);
        setUser(data.userData);
      } else {
        console.log("Failed to fetch user data");
      }
    } catch (error) {
      console.log("Error fetching user data", error);
    }
  };

  useEffect(() => {
    if (token) {
      userAuthentication();
    } else {
      setUser(null);  // Clear user data if no token
    }
  }, [token]);  

  return (
    <AuthContext.Provider value={{ isLoggedIn, storeTokenInLS, LogoutUser, user }}>
      {children}  {/* Render children components */}
    </AuthContext.Provider>
  );
};

// 3. Delivery or distribute
export const useAuth = () => {
  const authContextValue = useContext(AuthContext);
  if (!authContextValue) {
    throw new Error("useAuth must be used within AuthProvider");
  }
  return authContextValue;
};
