import React, { useState, useEffect } from "react";
import ProjectCard from "../components/ProjectCard"; // Import ProjectCard
import { fetchProjects } from "../services/api"; // Import the service function

const URL = "http://localhost:4000/api/v1/projects";

const HomePage = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  
 const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(URL, {
        method: "GET", 
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(user),
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Error: ${response.status} - ${errorText}`);
      }

      const res_data = await response.json();

      setProjects(res_data);

    } catch (error) {
      console.error("Login failed:", error.message);
    }
  };


  useEffect(() => {
    const loadProjects = async () => {
      try {
        setLoading(true); // Set loading state
        const data = await fetchProjects();
        setProjects(data); // Update state with fetched data
      } catch (err) {
        setError("Failed to fetch projects. Please try again later.");
      } finally {
        setLoading(false); // Reset loading state
      }
    };

    loadProjects();
  }, []);

  if (loading) {
    return <p>Loading projects...</p>;
  }

  if (error) {
    return <p className="text-red-500">{error}</p>;
  }

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col justify-center items-center text-center p-6">
      <header className="mb-12">
        <h1 className="text-5xl font-extrabold text-blue-600 mb-4">
          Welcome to Our Projects!
        </h1>
        <p className="text-xl text-gray-700">
          Discover our innovative platform and stay updated with the latest progress!
        </p>
      </header>

      <section className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
        {projects.map((project) => (
          <ProjectCard key={project.id} project={project} />
        ))}
      </section>

      <footer className="mt-12 text-gray-600">
        <p>&copy; 2024 Your Project Name. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default HomePage;
