import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { fetchProjectById } from '../services/api';

const ProjectDetails = () => {
  const [project, setProject] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  const { projectId } = useParams(); // Get the projectId from the URL params

  useEffect(() => {
    const getProjectDetails = async () => {
      try {
        setLoading(true);
        const data = await fetchProjectById(projectId); // Fetch project details using API service
        setProject(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    getProjectDetails();
  }, [projectId]); // Fetch when the projectId changes

  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <p className="text-lg">Loading...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex justify-center items-center h-screen">
        <p className="text-red-500">Error: {error}</p>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-8">
      <div className="bg-white shadow-lg rounded-lg p-6">
        <h1 className="text-3xl font-bold mb-4">{project?.name}</h1>
        <p className="text-xl text-gray-700 mb-6">{project?.description}</p>
        <div className="space-y-4">
          <div>
            <h3 className="font-semibold text-gray-600">Status</h3>
            <p>{project?.status}</p>
          </div>
          <div>
            <h3 className="font-semibold text-gray-600">Start Date</h3>
            <p>{new Date(project?.startDate).toLocaleDateString()}</p>
          </div>
          <div>
            <h3 className="font-semibold text-gray-600">End Date</h3>
            <p>{new Date(project?.endDate).toLocaleDateString()}</p>
          </div>
        </div>
        <div className="mt-6">
          <h3 className="text-lg font-semibold">Team Members</h3>
          <ul className="list-disc pl-6">
            {project?.teamMembers.map((member) => (
              <li key={member.id} className="text-gray-700">{member.name}</li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default ProjectDetails;
