import React, { useState, useEffect } from "react";
import ProjectCard from "../components/ProjectCard";
import { fetchProjects } from "../services/api";

const ProjectExplore = () => {
  const [projects, setProjects] = useState([]); // State for projects
  const [loading, setLoading] = useState(true); // State for loading indicator
  const [error, setError] = useState(null); // State for error messages
  const [newProject, setNewProject] = useState({
    name: "",
    description: "",
    startDate: "",
    endDate: "",
    status: "not started",
    teamMembers: [],
  }); // State for new project form

  // Function to fetch projects from the API
  const loadProjects = async () => {
    try {
      setLoading(true);
      const data = await fetchProjects();
      setProjects(data);
    } catch (err) {
      setError("Failed to fetch projects. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  // Function to handle form submission and create a new project
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:4000/api/v1/projects", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newProject),
      });

      if (!response.ok) {
        throw new Error("Failed to create project");
      }

      const result = await response.json();
      alert("Project created successfully!");
      setProjects((prev) => [...prev, result]); // Add the new project to the existing list
      setNewProject({
        name: "",
        description: "",
        startDate: "",
        endDate: "",
        status: "not started",
        teamMembers: [],
      });
    } catch (error) {
      console.error("Error creating project:", error);
    }
  };

  // Fetch projects when the component mounts
  useEffect(() => {
    loadProjects();
  }, []);

  // Display loading indicator while fetching
  if (loading) {
    return <p className="text-center text-lg">Loading projects...</p>;
  }

  // Display error message if fetching fails
  if (error) {
    return <p className="text-red-500 text-center">{error}</p>;
  }

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col justify-center items-center text-center p-6">
      {/* Header */}
      <header className="mb-12">
        <h1 className="text-5xl font-extrabold text-blue-600 mb-4">
          Explore Our Projects!
        </h1>
        <p className="text-xl text-gray-700">
          Discover innovative projects and stay updated with the latest progress!
        </p>
      </header>

      {/* Add New Project Form */}
      <section className="w-full max-w-lg mb-12">
        <form onSubmit={handleSubmit} className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
          <h2 className="text-2xl font-bold mb-4">Add a New Project</h2>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-bold mb-2">Name</label>
            <input
              type="text"
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700"
              value={newProject.name}
              onChange={(e) => setNewProject({ ...newProject, name: e.target.value })}
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-bold mb-2">Description</label>
            <textarea
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700"
              value={newProject.description}
              onChange={(e) => setNewProject({ ...newProject, description: e.target.value })}
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-bold mb-2">Start Date</label>
            <input
              type="date"
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700"
              value={newProject.startDate}
              onChange={(e) => setNewProject({ ...newProject, startDate: e.target.value })}
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-bold mb-2">End Date</label>
            <input
              type="date"
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700"
              value={newProject.endDate}
              onChange={(e) => setNewProject({ ...newProject, endDate: e.target.value })}
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-bold mb-2">Status</label>
            <select
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700"
              value={newProject.status}
              onChange={(e) => setNewProject({ ...newProject, status: e.target.value })}
            >
              <option value="not started">Not Started</option>
              <option value="in progress">In Progress</option>
              <option value="completed">Completed</option>
            </select>
          </div>
          <button
            type="submit"
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
          >
            Create Project
          </button>
        </form>
      </section>

      {/* Projects Section */}
      <section className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
        {projects.map((project) => (
          <ProjectCard key={project.id} project={project} />
        ))}
      </section>

      {/* Footer */}
      <footer className="mt-12 text-gray-600">
        <p>&copy; 2024 Your Project Name. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default ProjectExplore;
