import React from 'react';
import { Link } from 'react-router-dom';
import ProjectCard from '../components/ProjectCard';  // Import the ProjectCard component

const HomePage = () => {
  const projects = [
    {
      id: 1,
      name: 'Project Alpha',
      description: 'A cutting-edge project focused on AI and Machine Learning.',
      progress: 70,
      status: 'In Progress',
    },
    {
      id: 2,
      name: 'Project Beta',
      description: 'A blockchain-based project for secure transactions.',
      progress: 50,
      status: 'In Progress',
    },
    {
      id: 3,
      name: 'Project Gamma',
      description: 'A mobile app development project for e-commerce.',
      progress: 85,
      status: 'Completed',
    },
  ];

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col justify-center items-center text-center p-6">
      <header className="mb-12">
        <h1 className="text-5xl font-extrabold text-blue-600 mb-4">
          Welcome to Our Project!
        </h1>
        <p className="text-xl text-gray-700">
          Discover our innovative platform and stay updated with the latest progress!
        </p>
      </header>

      <section className="mb-12">
        <Link
          to="/projects"
          className="px-6 py-3 bg-blue-600 text-white text-lg rounded-lg shadow-md hover:bg-blue-700 transition duration-300"
        >
          Explore Projects
        </Link>
      </section>

      <section className="grid grid-cols-1 md:grid-cols-3 gap-12 mb-12">
        {projects.map((project) => (
          <ProjectCard key={project.id} project={project} />
        ))}
      </section>

      <footer className="mt-12 text-gray-600">
        <p>&copy; 2024 Your Project Name. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default HomePage;
